package com.javaex.practice;

public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 1;
		int y = 1;
		
		int a = ++x *2; // 4
		int b = y++ *2; // 2
		
		System.out.println("a=" + a); // 4
		System.out.println("b=" + b); // 4
		System.out.println("x=" + x); // 4
		System.out.println("Y=" + y); // 4
	
		
	}

}
